mapper.insert:
insert
  into
			 feed
			 (
			 	 feed_content
			 , feed_url
			 , feed_id
			 )
  		 values
  		 (
  		 	 #{feedContent}
  		 , #{feedURL}
  		 , #{feedId}
  		 )

mapper.update:
update
			 feed
   set
       feed_content = #{feedContent}
     , feed_modify_date = CURRENT_TIMESTAMP
     , feed_url = #{feedURL}
 where
 			 feed_no = #{feedNo}

